mapper.update:
update tb_board
   set group_bno = #{bNo}
   where bno = #{bNo}

update tb_board
	set title = #{title},
		content = #{content}
	where bno = #{bNo}

update tb_board
   set view_cnt = view_cnt + 1
 where bno = #{bNo}

update tb_board
   set group_blist = group_blist + 1
   where group_bNo = #{groupBno} and group_blist > #{groupBlist}

mapper.insert:
insert into tb_board(
	anonymous_id, title, content, group_blist, depth, group_bno 
)values(
	#{anonymousId}, #{title}, #{content}, #{groupBlist}+1, #{depth}+1, #{groupBno}
)

insert into tb_file (
	bNo, path, ori_name, sys_name, file_size
) values (
	#{bNo}, #{path}, #{oriName}, #{sysName}, #{fileSize}		
)

mapper.select:
select count(*)
from tb_comment
where bno = #{bNo}
